create table "public"."eav_attributes" (
    "id" bigint generated by default as identity not null,
    "created_at" timestamp with time zone not null default now(),
    "attribute_name" text not null,
    "data_type" character varying not null,
    "organization_id" uuid not null,
    "entity_id" uuid not null
);


alter table "public"."eav_attributes" enable row level security;

create table "public"."eav_entities" (
    "id" uuid not null default gen_random_uuid(),
    "created_at" timestamp with time zone not null default now(),
    "name" text not null,
    "organization_id" uuid not null
);


alter table "public"."eav_entities" enable row level security;

create table "public"."eav_values" (
    "created_at" timestamp with time zone not null default now(),
    "value_text" text,
    "value_int" bigint,
    "value_float" double precision,
    "value_datetime" timestamp with time zone,
    "artibutes_id" bigint not null,
    "id" bigint not null,
    "organization_id" uuid not null
);


alter table "public"."eav_values" enable row level security;

CREATE UNIQUE INDEX eav_attributes_pkey ON public.eav_attributes USING btree (id);

CREATE UNIQUE INDEX eav_entities_pkey ON public.eav_entities USING btree (id);

CREATE UNIQUE INDEX eav_values_pkey ON public.eav_values USING btree (id);
